import React, { useState } from 'react'
import { getUserDetails } from '../utils/api';
import { useNavigate, Link } from "react-router-dom";
import NavBar from '../components/NavBar';
import randomCodeGenerator from '../utils/randomCodeGenerator'

function Dashboard (){
    let navigate = useNavigate();

    const[user,setUser] = useState(true);
    const[loading, setLoading] = useState(true);
    const[roomCode, setRoomCode] = useState('')

    React.useEffect ( () => {
        getUserDetails()
        .then(({data}) => {
            setUser(data);
            setLoading(true);
        }).catch((err) => 
        navigate('/'));
        setLoading(false);
    },[])

    return !loading && (
      <div>
        <NavBar user={user}  />    <br/>
        <img src={process.env.PUBLIC_URL+"/img/quiestce2.png"} height="300"/>
        <input type='text' placeholder='Game Code' onChange={(event) => setRoomCode(event.target.value)} />
        <Link to={`/lobby?roomCode=${roomCode}`}>
          <button className="game-button green">JOIN GAME</button>
        </Link>
        <Link to={`/lobby?roomCode=${randomCodeGenerator(5)}`}>
          <button className="game-button orange">CREATE GAME</button>
        </Link>
      </div>
    )
  }

export default Dashboard;
