import React, { useEffect, useState } from 'react'
import io from 'socket.io-client'
import { getUserDetails,getCharacters } from '../utils/api';
import { useLocation, useNavigate, Link } from "react-router-dom";
import queryString from 'query-string'
import { useContext } from 'react';
import { UserContext } from '../App';
import ReactLoading from 'react-loading';

function Lobby(){
    const user = useContext(UserContext);
    const location = useLocation();
    const data = queryString.parse(location.search)
    const URL = process.env.REACT_APP_API_URL;

    let navigate = useNavigate();
    const[loading, setLoading] = useState(true);
    const[characters, setCharacters] = useState([]);

    useEffect ( () => {
      getUserDetails()
      .then(({}) => {
          setLoading(false);
      }).catch((err) => 
      navigate('/home'));
    },[])

    useEffect(() => {
      getCharacters()
      .then(({data}) =>{
        setCharacters(data)
      })
    },[])
    
    const [room, setRoom] = useState(data.roomCode)
    const [roomFull, setRoomFull] = useState(false)
    const [nbPlayer, setNbPlayer] = useState(1)
    const [player2, setPlayer2] = useState(null)

    //initialize socket state
    useEffect(() => {
        const connectionOptions =  {
            "forceNew" : true,
            "reconnectionAttempts": "Infinity", 
            "timeout" : 10000,                  
            "transports" : ["websocket"]
        }
        const socket = io.connect(URL, connectionOptions)
        console.log("CONNECT")
        
        socket.emit('join', {room: room, user: user.username, discordId:user.discordId, avatar:user.avatar}, (error) => {
          console.log("JOINING"+user)
            if(error)
                setRoomFull(true)
        })
        socket.on('roomData', (payload, cb) => {
          setNbPlayer(payload.users.length)
          if(payload.newUser.name == user.username){
            setPlayer2(payload.users[0])
          }
          else
            setPlayer2(payload.newUser)
        })

        //cleanup on component unmount
        return function cleanup() {
          socket.disconnect()
          //shut down connnection instance
          socket.off()
      }
    }, [])
    return !loading && (
      <div className='page-guess'>
        <div className='base-guess'>
            <div className='titre-guess'>
                <span className='span-code'>Game Code: {room}</span>
            </div>

            <div className='player-lobby'>
              <div className='player'>
                <img
                src={"https://cdn.discordapp.com/avatars/"+user.discordId+"/"+user.avatar+".png"}
                className="rounded-circle" height="100"/><br/>
                <span className='pseudo-guess'>{user.username}</span>
              </div>
              <div className='player'>
                {nbPlayer==2 ? (
                <><img
                  src={"https://cdn.discordapp.com/avatars/" + player2.discordId + "/" + player2.avatar + ".png"}
                  className="rounded-circle" height="100" /><br /><span className='pseudo-guess'>{player2.name}</span></>
                ) : (
                  <><span className='waiting'>Waiting <br/> Opponent 
                  <ReactLoading className="loading" type={'bubbles'} color={'white'} height={20} width={30} /></span>
                  </>
                )}
              </div>
            </div>

            <div className='start-lobby'>
              <Link className='d-grid gap-2 col-6 mx-auto' to={`/guesswho/lobby`}>
                    {nbPlayer==2 ? (
                    <button type="button" className="btn btn-primary">START</button>
                    ) : (
                      <button type="button" className="btn btn-primary disabled">START</button>
                    )
                    } 
                </Link>
            </div>
            <div className='quit-lobby'>
                <Link className='d-grid gap-2 col-6 mx-auto' to={`/guesswho`}>
                    <button type="button" className="btn btn-danger">QUIT</button>
                </Link>
            </div>                
        </div>        
      </div>
    )
  }

export default Lobby;
