import React, { useEffect, useState } from 'react'
import io from 'socket.io-client'
import { getUserDetails,getCharacters } from '../utils/api';
import { useLocation, useNavigate, Link } from "react-router-dom";
import queryString from 'query-string'
import { useContext } from 'react';
import { UserContext } from '../App';

function Lobby(){
    const user = useContext(UserContext);
    const location = useLocation();
    const data = queryString.parse(location.search)
    const URL = process.env.REACT_APP_API_URL;

    let navigate = useNavigate();
    const[loading, setLoading] = useState(true);
    const[characters, setCharacters] = useState([]);

    useEffect ( () => {
      getUserDetails()
      .then(({}) => {
          setLoading(false);
      }).catch((err) => 
      navigate('/home'));
    },[])

    useEffect(() => {
      getCharacters()
      .then(({data}) =>{
        setCharacters(data)
      })
    },[])
    //initialize socket state
    const [room, setRoom] = useState(data.roomCode)
    const [roomFull, setRoomFull] = useState(false)

    useEffect(() => {
        const connectionOptions =  {
            "forceNew" : true,
            "reconnectionAttempts": "Infinity", 
            "timeout" : 10000,                  
            "transports" : ["websocket"]
        }
        const socket = io.connect(URL, connectionOptions)
        console.log("CONNECT")
        
        socket.emit('join', {room: room, user: user.username}, (error) => {
          console.log("JOINING"+user)
            if(error)
                setRoomFull(true)
        })
        socket.on('roomData', (payload, cb) => {
          console.log(payload.users)
        })

        //cleanup on component unmount
        return function cleanup() {
          socket.disconnect()
          //shut down connnection instance
          socket.off()
      }
    }, [])
    return !loading && (
      <div>
        <button>{room}</button>
        <Link to={'/guesswho'}>
          <button className="game-button green">Quitter</button>
        </Link>
        {characters.map((perso)=>{
            return <p key={perso.name}>{perso.name}</p>
          })}
      </div>
    )
  }

export default Lobby;
