import React, { useEffect, useState } from 'react'
import io from 'socket.io-client'
import { getUserDetails } from '../utils/api';
import { useLocation, useNavigate, Link } from "react-router-dom";
import queryString from 'query-string'
import NavBar from '../components/NavBar';
import { useContext } from 'react';
import { UserContext } from '../App';

function GuessWho(){
    const user = useContext(UserContext);
    const location = useLocation();
    const[roomCode, setRoomCode] = useState('')

    let navigate = useNavigate();
    const[loading, setLoading] = useState(true);

    useEffect ( () => {
      getUserDetails()
      .then(({}) => {
          setLoading(false);
      }).catch((err) => 
      navigate('/'));
    },[])

    return !loading && (
      <div>
        <NavBar user={user}  />    <br/>
        
        <input type='text' placeholder='Game Code' onChange={(event) => setRoomCode(event.target.value)} />
        <Link to={`/lobby?roomCode=${roomCode}`}>
          <button className="game-button green">JOIN GAME</button>
        </Link>
        <Link to={`/lobby?roomCode=${randomCodeGenerator(5)}`}>
          <button className="game-button orange">CREATE GAME</button>
        </Link>
      </div>
    )
  }

export default GuessWho;
