import axios from "axios";

export function getUserDetails(){
    user=null
    axios.get("http://localhost:5000/api/user", {
        withCredentials: true
    }).then((data) => {
        auth = true;
        user = data.data;
    }).catch((err) => {
        auth = false;
    })
    return [auth,user]
}
