import React, {useState} from 'react'
import { getUserDetails } from '../utils/api';
import { useNavigate } from "react-router-dom";

function Home(){
    const login = () => window.location.href = 'http://localhost:5000/api/user/login'
    let navigate = useNavigate();

    const[user,setUser] = useState(true);
    const[loading, setLoading] = useState(true);
    React.useEffect ( () => {
        getUserDetails()
        .then(({data}) => {
            setUser(data);
            setLoading(false); 
            navigate('/dashboard')
        })
        setLoading(false);
    },[])
    return (
      <div>
          <img className='img-titre'src={process.env.PUBLIC_URL + '/img/titre.png'} />
        
        <div className='connection'>
          <button className='button-login' onClick = {login}>Connexion
          <img className='img-discord' src={process.env.PUBLIC_URL + '/img/discord.png'} /></button>
        </div>
      </div>
    )
  }

export default Home;
